// Script for making audio recordings during a lab.js trial
// To use, add this as a script to the 'before:prepare' stage
// of all screens for which you want an audio recording and
// adjust the configuration below

// --- Configuration ----------------------------------------------

// Automatically start / stop the recording on lab.js events?
// Event name (e.g., 'run', 'end') or 'false' to deactivate. 
// Note: browsers might block auto-recording if the user has 
// not interacted with the website at all. Adding a slide that 
// requires user interaction (e.g., a "test recording" slide) 
// should solve this issue.
const start_event = 'run';  
const stop_event = 'end';  

// Use recording interface on the page?
// HTML element ids or `false` to deactivate
const rec_button = false; 
const clip_list = false; 

// Store recording in lab.js data store (options.data) as base64 
// encoded string? 
// See https://labjs.readthedocs.io/en/latest/reference/data.html
const save_to_labjs_data = true;  

// Upload recording to JATOS? 
// By default it uses the title of the trial as the filename.
// Note: jatos.js library is only available in JATOS and must 
// probably be loaded first. If this option is `true` outside of 
// jatos it will cause an error in the console but the experiment
// will proceed regardless.
const save_to_jatos = true;    
const filename = `${ this.options.title.replace(/\s/g, '') }.ogg`;

// The audio codec to use for the recording. There are other 
// codecs, and their support varies between browsers. 
// Hopefully this one works across all modern browsers.
// ToDo: Check which codecs are supported and adapt automatically.
const audio_codec = 'audio/ogg; codecs=opus'

// ----------------------------------------------------------------


const c = this;
let mediaRecorder;

let setupRecorder = function(stream) {

  mediaRecorder = new MediaRecorder(stream);
  let chunks = [];  // buffer for audio
  let audioURL;         

  // called automatically when recording stops, 
  // piece together sound snippets from the buffer and store result
  mediaRecorder.onstop = function(e) {

    const blob = new Blob(chunks, { 'type' : audio_codec });

    let info = `Chunk length: ${ chunks.length }, blob size: ${ blob.size }.`
    info = info.concat(
      blob.size > 0 ? ' Looks good!' : ' Looks like no sound was recorded!'
    );

    if (save_to_labjs_data) {
      var reader = new FileReader();
      reader.readAsDataURL(blob);
      reader.onloadend = function() {
        c.options.datastore.set('filename', filename);
        c.options.datastore.set('audio', this.result);
      }
      info = info.concat(' Audio stored in lab.js data store.');
    }

    if (save_to_jatos) {
      jatos.uploadResultFile(blob, filename).done(() => { 
        info = info.concat(` Audio uploaded to JATOS as ${ filename }.`);
      });
    }

    if (clip_list) { 
      if (audioURL) {
        // TODO: remove old audio url to free memory
        // ...
      }
      audioURL = window.URL.createObjectURL(blob);
      showClip(audioURL);
    }


    console.info(info);
    chunks = [];
  }


  // called automatically during recording, 
  // fill buffer with sound snippets
  mediaRecorder.ondataavailable = function(e) {
    chunks.push(e.data);
  }
}



const activateRecButton = function() {
  const recBtn = document.getElementById(rec_button);
  recBtn.disabled = false;

  if (mediaRecorder.state == "recording") {
    recBtn.classList.add("recording")
  }
  
  recBtn.onclick = function(e) {
    e.preventDefault()

    if (mediaRecorder.state == "recording") {
      mediaRecorder.stop();
      recBtn.classList.remove("recording")
    } else {
      mediaRecorder.start();
      recBtn.classList.add("recording")
    }
  }
}



const showClip = function(audioURL) {
  const clip = document.createElement('article');
  const audio = document.createElement('audio');
  audio.src = audioURL;
  audio.controls = true;
  clip.appendChild(audio);
  
  // remove old clip, only show one clip at a time
  const clipList = document.getElementById(clip_list);
  clipList.removeChild(clipList.firstChild);
  clipList.appendChild(clip);
}



if (navigator.mediaDevices.getUserMedia) {
    navigator.mediaDevices
    .getUserMedia({ audio: true })
    .then(setupRecorder, (err) => {
      console.warn('The following error occured: ' + err);
    })
    .then(function() {
      // set up event listeners
      if (start_event) c.on(start_event, function() {
        mediaRecorder.start()
        console.log("Recording started automatically...")
      });

      if (stop_event) c.on(stop_event, function() {
        mediaRecorder.stop()
      });

      // (html elements are not available before the 'run' event!)
      if (rec_button) c.on('run', function() {
        activateRecButton()
      });
    });

} else {
    // TODO: use lab.js to abort experiment 
    alert.log('Audio recording not supported by your browser.');
}

save_to_jatos = true;

if (save_to_jatos) {
  const blob = this.options.datastore.exportBlob(filetype='csv');
  const filename = 'results.csv';

  jatos.uploadResultFile(blob, filename).done(() => { 
    info = info.concat(`CSV uploaded to JATOS as ${ filename }.`);
  });
}

